            $('.bbp-message').addClass('alert-success').removeClass('alert-primary alert-danger').text('Registration successful. Please login.').show();;
        <?php } ?>


        <?php if (get_the_ID() == 39747) { ?>
            var redirect = '<?= get_the_permalink(39318) ?>';
        <?php } else { ?>
            var redirect = jQuery('meta[property="og:url"]').attr('content');
        <?php } ?>

        $('body:not(.page-id-39754) .bbp-login-form').submit(function(e) {
            $('.bbp-message').addClass('alert-primary').removeClass('alert-danger alert-success').text('Logging in').show();
            e.preventDefault();
            var formData = $(this).serialize();

            // Add the nonce to the form data
            formData += '&action=bbpress_ajax_login&security=' + $('#_wpnonce').val();


            $.ajax({
                type: 'POST',
                url: 'https://theribbonbox.com/wp-admin/admin-ajax.php',
                data: formData,
                dataType: 'json',
                success: function(response) {
                    $('.bbp-message').removeClass('alert-primary alert-danger alert-success').addClass(response.class).text(response.message).show();;
                    if (response.status == 'success') {
                        window.location.href = redirect;
                    }
                },
                error: function(xhr, status, error) {
                    console.error(error);
                    $('.bbp-message').removeClass('alert-primary alert-success').addClass('alert-danger').text('Login failed please try again.');
                }
            });
        });
    });
</script>

<?php if (is_front_page()) {
    $_SESSION['homepage_array'] = "";
} ?>

</body>

</html>